class Solution:
    def isSubsequence(self, s: str, t: str) -> bool:
        scount = 0
        if not s:
            return True
        if not t and s:
            return False 
        if len(s) == 1:
            return s in t
        for c in t:
            if scount > len(s) - 1:
                break
            if c == s[scount]:
                scount += 1
        
        return scount == len(s)

if __name__ == "__main__":
    solution = Solution()
    s = "abc"
    t = "ahbgdc"
    print(solution.isSubsequence(s, t))
    s = "leeeeetcode"
    t = ""
    print(solution.isSubsequence(s, t))